<?php

class establecimientos_web_services {

  static $barrios           = 'barrios';
  static $comunas           = 'comunas';
  static $distrito          = 'distritos-escolares';
  static $dependencia       = 'dependencias-funcionales';
  static $establecimiento   = 'tipos-establecimientos';
  static $jornada           = 'jornadas';
  static $orientacion       = 'orientaciones';
  static $sala              = 'salas';
  static $oferta            = 'ofertas';
  static $titulo            = 'titulos';
  static $url_web_service;

  static function getAPIestablecimientos($servicio) {
    $ch = curl_init(self::$url_web_service . "/$servicio?limit=1000"); // Colocamos un limite porque por defecto la API limita en 100 resultados por pagina

    curl_setopt($ch, CURLOPT_RETURNTRANSFER, TRUE);
    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, FALSE);
    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, FALSE);
    $server_output = curl_exec($ch);

    curl_close($ch);

    $json_output = json_decode($server_output, TRUE);

    return !empty($json_output['data']) ? $json_output['data'] : array();
  }
}

establecimientos_web_services::$url_web_service = variable_get('gcaba_establecimientos_url_web_service');

/* Esto es un mock para cuando se comenzó el desarrollo (para uso offline)
 *
 *
  establecimientos_web_services::

  static function getAPIestablecimientosOffline($servicio){
    switch($servicio){
        case self::$barrios: $server_output = self::obtenerBarrios();  break;
        case self::$comunas: $server_output = self::obtenerComunas();  break;
        case self::$distrito: $server_output = self::obtenerDistritoEscolar();  break;
        case self::$dependencia: $server_output = self::obtenerDependenciaFuncional();  break;
        case self::$establecimiento: $server_output = self::obtenerTipoEstablecimientos();  break;
        case self::$jornada: $server_output = self::obtenerJornadas();  break;
        case self::$orientacion: $server_output = self::obtenerOrientacion();  break;
        case self::$sala: $server_output = self::obtenerSalas();  break;
        case self::$oferta: $server_output = self::obtenerOfertas();  break;
        case self::$titulo: $server_output = self::obtenerTitulos();  break;
    }

    $json_output = json_decode($server_output, true);

    return !empty($json_output['data']) ? $json_output['data'] : array();
  }

